import React, { Component, useState } from "react";
import { useForm, ValidationError } from "@formspree/react";
import validator from "validator";

class ContactForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      state: "False",
      email: "",
    };
  }
  DisplaySuccess() {
    return (
      <div className="container   bg-light" style={{ padding: "5rem" }}>
        {" "}
        <p className="display-3">Thank you for contacting me!</p>
      </div>
    );
  }
  addEmail(e) {
    console.log(e.target.value);
  }
  verifyEmail(event) {
    if (validator.isEmail(event.target.value)) {
      document
        .getElementById("valid-feedback")
        .insertAdjacentText("afterbegin", "Looks Good!");
    } else {
      return document
        .getElementById("Invalid-feedback")
        .insertAdjacentText("afterbegin", "Invalid!");
    }
  }
  handleOnSubmit(event) {
    event.preventDefault();
    setServerState({ submitting: true });
    axios({
      method: "POST",
      url: "https://formspree.io/f/{form_id}",
      data: inputs,
    })
      .then((r) => {
        handleServerResponse(true, "Thanks!");
      })
      .catch((r) => {
        handleServerResponse(false, r.response.data.error);
      });
  }

  render() {
    return (
      <div className="container   bg-light" style={{ padding: "5rem" }}>
        <p
          style={{
            textAlign: "center",
            textCombineUpright: "all",
            fontSize: "20px",
            fontFamily: "cursive",
          }}
        >
          You can send me a message here! I will get back to you as soon as
          possible.
        </p>
        <form onSubmit={handleSubmit}>
          <div class="col-md-4">
            <label
              className="form-label"
              for="email"
              style={{
                fontFamily: "fantasy",
              }}
            >
              Your Email Address
            </label>
            <input
              className="form-control"
              id="email"
              type="email"
              name="email"
              onInput={(e) => addEmail(e)}
            />

            <div id="valid-feedback"></div>

            <ValidationError
              prefix="Email"
              field="email"
              errors={state.errors}
            />
            <div id="invalid-feedback" className="invalid-feedback"></div>
            <div id="valid-feedback" class="invalid-feedback">
              Please enter a message in the textarea.
            </div>
          </div>
          <div class="mb-3">
            {" "}
            <label className="form-label" htmlFor="validationTextarea">
              Your Message
            </label>
            <textarea
              className="form-control is-invalid"
              id="validationTextarea"
              name="message"
              required
            />
            <div class="invalid-feedback">
              Please enter a message in the textarea.
            </div>
            <ValidationError
              prefix="Message"
              field="message"
              className="valid-feedback"
              errors={state.errors}
            />
          </div>

          <button type="submit" disabled={state.submitting}>
            Submit
          </button>
        </form>
      </div>
    );
  }
}

export default ContactForm;
