import React, { useState } from "react";
import { useForm, ValidationError } from "@formspree/react";
import validator from "validator";
function DisplaySuccess() {
  return (
    <div className="container   bg-light" style={{ padding: "5rem" }}>
      {" "}
      <p className="display-3">Thank you for contacting me!</p>
    </div>
  );
}
function verifyEmail(event) {
  const target = event.target;
  const value = target.type === "email" ? target.value : "";
  console.log();
  document.getElementById("valid-feedback").removeChild();
  if (validator.isEmail(value)) {
    document
      .getElementById("valid-feedback")
      .insertAdjacentText("afterbegin", "Looks Good!");
  } else {
    return document
      .getElementById("valid-feedback")
      .insertAdjacentText("afterbegin", "Invalid!");
  }
}
var email = "";
const addEmail = (e) => {
  email += e.target.value;
};
function ContactForm() {
  const [state, handleSubmit] = useForm("xwkaqqeg");

  if (state.succeeded) {
    DisplaySuccess();
  }
  return (
    <div className="container   bg-light" style={{ padding: "5rem" }}>
      <p
        style={{
          textAlign: "center",
          textCombineUpright: "all",
          fontSize: "20px",
          fontFamily: "cursive",
        }}
      >
        You can send me a message here! I will get back to you as soon as
        possible.
      </p>
      <form onSubmit={handleSubmit}>
        <div class="col-md-4">
          <label
            className="form-label"
            for="email"
            style={{
              fontOpticalSizing: "2px",
              textShadow: "2px",
              textAlign: "center",
            }}
          >
            Enter Your Email Address
          </label>
          <input
            className="form-control"
            id="email"
            type="email"
            name="email"
            onInput={(e) => addEmail(e)}
          />
          {console.log(email)}
          {validator.isEmpty(email) ? "" : verifyEmail(email)}
          <div id="valid-feedback"></div>

          <ValidationError prefix="Email" field="email" errors={state.errors} />
          <div id="invalid-feedback" className="invalid-feedback">
            {}
          </div>
          <div id="valid-feedback" class="invalid-feedback">
            Please enter a message in the textarea.
          </div>
        </div>
        <div class="mb-3">
          {" "}
          <label className="form-label" htmlFor="validationTextarea">
            Your Message
          </label>
          <textarea
            className="form-control is-invalid"
            id="validationTextarea"
            name="message"
            required
          />
          <div class="invalid-feedback">
            Please enter a message in the textarea.
          </div>
          <ValidationError
            prefix="Message"
            field="message"
            className="valid-feedback"
            errors={state.errors}
          />
        </div>

        <button type="submit" disabled={state.submitting}>
          Submit
        </button>
      </form>
    </div>
  );
}

export default ContactForm;
