import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import profile from "./../assets/img/profile.png";
import { SocialIcon } from "react-social-icons";
import {
  motion,
  motionValue,
  useTransform,
  useViewportScroll,
} from "framer-motion";
import Aos from "aos";
import "aos/dist/aos.css";

const Section = styled.section`
  justify-conter: center;
  align-item: center;
  flex: "1";
  display: flex;
`;
const Container1 = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;
const MidContainer = styled.div`

  display: flex;
  flex:1
  flex-direction: column;
  justify-content: center;
  align-items: center;
  paddingLeft: "38px";
`;
const ColLeft = styled.div`
display:flex;
flex:0.3
flex-direction:column;
height:"100%",
justify-content:flex-end;
align-items:center;

`;
const ColRight = styled.div`
  display: flex;
  flex: 0.7;
  flex-direction: column;
  justify-content: flex-end;
  align-items: center;
`;

const contentOffsetY = motionValue(0);

const Home = () => {
  const ref = useRef();
  const [progress, setProgress] = useState(0);
  const { scrollY, scrollYProgress } = useViewportScroll();

  useEffect(() => {
    Aos.init({ duration: 2000 });
    // var x = scrollYProgress.onChange((v) => v);
    // return x;
    return scrollY.onChange((v) => setProgress(v));
  }, [scrollYProgress, scrollY]);
  console.log(progress);
  const scale = useTransform(scrollYProgress, [1, 0], [0, 1], {
    clamp: false,
  });

  return (
    <div>
      <motion.Section
        style={{
          scale,
        }}
        ref={ref}
        className="bg-dark"
      >
        <Container1
          style={{
            flex: 1,
            minHeight: "100vh",
            flexDirection: "column",
            flexWrap: "wrap",
            justifyContent: "center",
            alignItems: "center",
          }}
          data-aos="fade-left"
          clasName="bg-dark vh-100 "
        >
          <motion.div
            animate={{ scale: [2, 1] }}
            style={{
              maxHeight: "300px",
              maxWidth: "300px",
              borderRadius: "50%",
              marginBottom: "15px",
              flex: 1,
              justifyContent: "flex-end",
              alignItems: "center",
              alignContent: "space-around",
            }}
          >
            <img
              src={profile}
              className="rounded-circle"
              style={{ height: "auto", width: "100%" }}
            ></img>
          </motion.div>
          <h2
            style={{
              color: "#ffff",

              textAlign: "center",
            }}
            class="display-3  "
          >
            Suman Karanjit
          </h2>

          <h6 class=" " style={{ color: "#6c757d", textAlign: "center" }}>
            Aspiring software developer experienced in object-oriented
            programming; developing, testing and debugging code; designing
            interfaces; and administering systems and networks.
          </h6>
        </Container1>
      </motion.Section>
      <div style={{ display: "flex", width: "100%" }}>
        <motion.div
          id="sidebar sticky"
          className="bg-light"
          style={
            progress > 354
              ? {
                  display: "flex",
                  flex: 1,
                  flexDirection: "column",
                  alignItems: "center",
                  justifyContent: "flex-start",
                  width: "90px",
                  position: "fixed",
                  top: "0%",

                  height: "90vh",
                  zIndex: "999",

                  paddingTop: "90px",
                  paddingLeft: "4px",

                  justifyContent: "right",
                  marginRight: "10%",
                }
              : {
                  display: "none",
                }
          }
        >
          <motion.div
            animate={progress > 345 ? { scale: [3, 1] } : {}}
            transition={{ duration: 0.5 }}
            style={{
              maxHeight: "80px",
              width: "80px",
              borderRadius: "50%",
              justifyContent: "center",
              display: "flex",
              flex: 1,
              flexDirection: "column",
              alignItems: "center",
              alignContent: "space-around",
            }}
          >
            <img
              src={profile}
              className="rounded-circle"
              style={{ height: "auto", width: "100%" }}
            ></img>
            <h6 style={{ color: "darkslategray" }}>SK</h6>
          </motion.div>

          <motion.div
            id="connect"
            animate={progress > 348 ? { scale: [0.2, 1] } : {}}
            transition={{ duration: 1 }}
            style={{
              display: "flex",
              flex: "0.1",
              flexDirection: "row",

              alignItems: "flex-start",
            }}
          >
            <SocialIcon
              url="https://github.com/skaranjit"
              style={{ height: 25, width: 25, margin: "4px" }}
            />
            <SocialIcon
              network="email"
              url="#Contact"
              style={{ height: 25, width: 25, margin: "4px" }}
            />
            <SocialIcon
              url="https://www.linkedin.com/in/suman-karanjit-967373211/"
              style={{ height: 25, width: 25, margin: "4px" }}
            />
          </motion.div>
          <div
            style={{
              display: "flex",
              flex: "0.8",
              flexDirection: "column",
              justifyContent: "flex-end",
              marginLeft: "25px",
              alignItems: "flex-start",
            }}
          >
            <motion.div
              initial={{ opacity: 0 }}
              animate={
                progress > 354
                  ? {
                      opacity: 1,
                      transition: { delay: 2.6, duration: 1.5 },
                    }
                  : {}
              }
            >
              <img
                alt="HTML5"
                src="https://img.shields.io/badge/html5-%23E34F26.svg?style=for-the-badge&logo=html5&logoColor=white"
              />
            </motion.div>
            <motion.div
              initial={{ opacity: 0 }}
              animate={
                progress > 354
                  ? {
                      opacity: 1,
                      transition: { delay: 2.4, duration: 1.5 },
                    }
                  : {}
              }
            >
              <img
                alt="CSS3"
                src="https://img.shields.io/badge/css3-%231572B6.svg?style=for-the-badge&logo=css3&logoColor=white"
              />
            </motion.div>
            <motion.div
              initial={{ opacity: 0 }}
              animate={
                progress > 354
                  ? {
                      opacity: 1,
                      transition: { delay: 2.2, duration: 1.5 },
                    }
                  : {}
              }
            >
              <img
                alt="Python"
                src="https://img.shields.io/badge/python-%2314354C.svg?style=for-the-badge&logo=python&logoColor=white"
              />
            </motion.div>
            <motion.div
              initial={{ opacity: 0 }}
              animate={
                progress > 354
                  ? {
                      opacity: 1,
                      transition: { delay: 2, duration: 1.5 },
                    }
                  : {}
