import React, { Component } from "react";
import { Container, Navbar, Nav } from "react-bootstrap";
import { motion } from "framer-motion";
import logo from "./../../assets/logos/logo.png";

class MenuBar extends Component {
  state = {
    navExpanded: false,
    setShow: false,
    haveAccount: false,
    registered: false,
    isOpen: "open",
    selected: "home",
  };
  constructor() {
    super();

    this.variant = {
      open: { opacity: 1, x: 0 },
      closed: { opacity: 0, x: "-100%" },
    };
  }
  setExpanded() {
    this.setState({
      navExpanded: this.state.navExpanded === false ? "exapanded" : false,
    });
  }

  handleClose() {
    this.setState({
      setShow: false,
    });
  }
  handleShow() {
    this.setState({
      setShow: true,
    });
  }

  render() {
    return (
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark px-3">
        <div className="container-fluid">
          <button
            onClick={() =>
              setTimeout(() => {
                this.setExpanded();
              }, 150)
            }
            class="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarTogglerDemo03"
            aria-controls="navbarTogglerDemo03"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon"></span>
          </button>
          <a class="navbar-brand" href="#">
            <motion.img
              animate={{
                scale: [1, 2, 2, 1, 1],
                rotate: [0, 0, 270, 270, 0],
                borderRadius: ["20%", "20%", "50%", "50%", "20%"],
              }}
              whileHover={{
                scale: 3,
                rotate: [0, 0, 270, 270, 0],
              }}
              src={logo}
              width="40px"
              height="40px"
            ></motion.img>
          </a>

          <div className="collapse navbar-collapse" id="navbarTogglerDemo03">
            <ul
              className="navbar-nav me-auto mb-2 mb-lg-0"
              defaultChecked={this.state.selected}
            >
              <li className="nav-item">
                <a
                  onClick={() =>
                    this.setState({
                      navExpanded: false,
                      selected: "home",
                    })
                  }
                  className={
                    this.state.selected == "home" ? "nav-link active" : ""
                  }
                  aria-current="page"
                  href="#"
                >
                  Home
                </a>
              </li>
              <li
                onClick={() =>
                  this.setState({
                    navExpanded: false,
                    selected: "About",
                  })
                }
                class="nav-item"
                className={this.state.selected == "About" ? "active" : ""}
              >
                <a class="nav-link" href="#About">
                  About
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#Contact" tabindex="-1">
                  Contact
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    );
  }
}

export default MenuBar;
