import React, { useLayoutEffect, useRef, useState } from "react";
import styled from "styled-components";
import Profile from "./Profile";
import profile from "./../assets/img/profile.png";
import {
  motion,
  motionValue,
  useMotionValue,
  useTransform,
  useViewportScroll,
} from "framer-motion";
import { Component } from "react";
import Aos from "aos";
import "aos/dist/aos.css";
import { auto } from "@popperjs/core";

const Section = styled.section`
  justify-conter: center;
  align-item: center;
  display: flex;
`;
const Container1 = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;
const ColumnRight = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex:1
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;
const contentOffsetY = motionValue(0);

const Home = () => {
  const ref = useRef();
  const [start, setStart] = useState(null);
  const [end, setEnd] = useState(null);
  useLayoutEffect(() => {
    Aos.init({ duration: 2000 });
    if (!ref.current) {
      return;
    }
    const rect = ref.current.getBoundingClientRect();
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    const offsetTop = rect.top + scrollTop;
    setStart(offsetTop / document.body.clientHeight);
    setEnd((offsetTop + rect.height) / document.body.clientHeight);
  }, []);

  const [progress, setProgress] = useState(0);
  const { scrollY, scrollYProgress } = useViewportScroll();

  // Apply this override to your scroll component

  const x = useMotionValue(0);
  const scale = useTransform(scrollYProgress, [1, 0], [0, 1], {
    clamp: false,
  });

  console.log(scrollY, scrollYProgress);
  return (
    <div>
      <motion.Section
        style={{
          scale,
        }}
        ref={ref}
        className="bg-dark   "
      >
        <motion.div className="top-50 start-100 p-5 vh-100 ">
          <Container1
            style={{ background: "#0000", height: "100%" }}
            data-aos="fade-left"
