import React, { useState, useEffect, Component } from "react";
import axios from "axios";
import {
  motion,
  motionValue,
  useTransform,
  useViewportScroll,
} from "framer-motion";

class ContactForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: {},
      errors: {},
      setServerStatus: {},
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      input,
    });
  }
  handleServerResponse(ok, msg) {
    let serverStatus = {};
    serverStatus["submitting"] = true;
    serverStatus["status"] = { ok, msg };

    this.setState({
      setServerStatus: serverStatus,
    });
    if (ok) {
      setInputs({
        email: "",
        message: "",
      });
    }
  }
  handleSubmit(event) {
    event.preventDefault();

    if (this.validate()) {
      console.log(this.state);
      let input = {};
      input["name"] = "";
      input["email"] = "";
      input["message"] = "";
      this.setState({ input: input });

      alert("Your request have been submited sucessfully");
    }
  }
  validate() {
    let input = this.state.input;
    let errors = {};
    let isValid = true;

    if (!input["name"]) {
      isValid = false;
      errors["name"] = "Please enter your name.";
    }

    if (!input["email"]) {
      isValid = false;
      errors["email"] = "Please enter your email Address.";
    }

    if (typeof input["email"] !== "undefined") {
      var pattern = new RegExp(
        /^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i
      );
      if (!pattern.test(input["email"])) {
        isValid = false;
        errors["email"] = "Please enter valid email address.";
      }
    }

    if (!input["message"]) {
      isValid = false;
      errors["message"] = "Please enter your message.";
    }

    this.setState({
      errors: errors,
    });

    return isValid;
  }

  render() {
    return (
      <div
        className="container"
        style={{
          display: "flex",
          top: 90,
          flex: 1,
          flexDirection: "column-reverse",
          justifyContent: "center",
          alignContent: "center",
          height: "100vh",
        }}
      >
        <form onSubmit={this.handleSubmit}>
          <div class="form-group">
            <label for="name">Your Name:</label>
            <input
              type="text"
              name="name"
              value={this.state.input.name}
              onChange={this.handleChange}
              class="form-control"
              placeholder="Enter name"
              id="name"
            />

            <div className="text-danger">{this.state.errors.name}</div>
          </div>
          <div class="form-group">
            <label for="email">Your Email Address:</label>
            <input
              type="text"
              name="email"
              value={this.state.input.email}
              onChange={this.handleChange}
              class="form-control"
              placeholder="Enter email"
              id="email"
            />

            <div className="text-danger">{this.state.errors.email}</div>
          </div>
          <div class="form-group">
            <label for="message">Message:</label>
            <textarea
              name="message"
              value={this.state.input.message}
              onChange={this.handleChange}
              placeholder="Your message...."
              class="form-control"
            />

            <div className="text-danger">{this.state.errors.message}</div>
          </div>

          <button type="submit" value="Submit" class="btn btn-success">
            Submit
          </button>
        </form>
      </div>
    );
  }
}
