import React from "react";
import {
  motion,
  motionValue,
  useTransform,
  useViewportScroll,
} from "framer-motion";
import Aos from "aos";
import "aos/dist/aos.css";
function Edu(props) {
  return (
    <section
      style={{
        width: "80vw",
        flex: 1,
        display: "flex",
        flexDirection: "row",
        justifyContent: "flex-start",
        top: 0,
        alignContent: "flex-start",
        marginLeft: "20vw",
      }}
      className="bg-light "
    >
      <motion.div
        style={{
          display: "flex",
          position: "sticky",
          flex: 0.3,
          flexDirection: "column",
          justifyContent: "center",
          alignContent: "center",
          height: "100vh",
        }}
      >
        <h2
          data-aos="fade-right"
          style={{
            paddingBottom: "10px",
          }}
          class="display-4"
        >
          Education
        </h2>
      </motion.div>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: [0, 0.5, 1] }}
        transition={{ duration: 5 }}
      >
        <h3>Bachelor's in Computer Science</h3>
        MSUM, Moorhead, MN, USA (Graduated May 2021)
      </motion.div>
    </section>
  );
}

export default Edu;
