import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import styled from "styled-components";
import Profile from "./Profile";
import profile from "./../assets/img/profile.png";
import { SocialIcon } from "react-social-icons";
import {
  motion,
  motionValue,
  useMotionValue,
  useTransform,
  useViewportScroll,
} from "framer-motion";
import { Component } from "react";
import Aos from "aos";
import "aos/dist/aos.css";
import { auto } from "@popperjs/core";
import postcssFlexbugsFixes from "postcss-flexbugs-fixes";

const Section = styled.section`
  justify-conter: center;
  align-item: center;
  display: flex;
`;
const Container1 = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;
const ColumnRight = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex:1
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;

const contentOffsetY = motionValue(0);

const Home = () => {
  const ref = useRef();
  const [progress, setProgress] = useState(0);
  const { scrollY, scrollYProgress } = useViewportScroll();
  useEffect(() => {
    Aos.init({ duration: 2000 });
    // var x = scrollYProgress.onChange((v) => v);
    // return x;
    return scrollY.onChange((v) => setProgress(v));
  }, [scrollYProgress, scrollY]);

  const scale = useTransform(scrollYProgress, [1, 0], [0, 1], {
    clamp: false,
  });

  console.log(scrollY, scrollYProgress);
  return (
    <div>
      <motion.Section
        style={{
          scale,
        }}
        ref={ref}
        className="bg-dark   "
      >
        <motion.div className="top-50 start-100 p-5 vh-100 ">
          <Container1
            style={{ background: "#0000", height: "100%" }}
            data-aos="fade-left"
            clasName="bg-dark vh-100 "
          >
            <motion.div
              animate={{ scale: [5, 1] }}
              style={{
                height: "200px",
                width: "200px",
                borderRadius: "50%",
                background: "white",
                alignSelf: "center",
                justifyContent: "center",
              }}
            >
              <img
                src={profile}
                className="rounded-circle"
                style={{ height: "auto", width: "100%" }}
              ></img>
            </motion.div>
            <motion.div
              animate={{ scale: [5, 1] }}
              transition={{ duration: 0.9 }}
              style={{
                justifyContent: "center",
                alignSelf: "center",
              }}
            >
              <h2
                style={{ color: "#ffff", padding: 0, margin: 0 }}
                class="display-3  bottom-50 end-50"
              >
                Suman Karanjit
              </h2>
              <h4 class=" " style={{ color: "#6c757d" }}>
                Software Developer
              </h4>
            </motion.div>
          </Container1>
        </motion.div>
      </motion.Section>
      <motion.div style={{ display: "flex", width: "100%" }}>
        <motion.div
          animate={progress > 345 ? { scale: [0.3, 1] } : {}}
          id="sidebar sticky"
          className="bg-light"
          style={
            progress > 354
              ? {
                  display: "flex",
                  flex: 1,
                  flexDirection: "column",
                  alignItems: "center",
                  justifyContent: "flex-start",
                  width: "90px",
                  position: "fixed",
                  top: "0%",

                  height: "100vh",
                  zIndex: " 999",

                  paddingTop: "90px",
                  paddingLeft: "4px",

                  justifyContent: "right",
                  marginRight: "10%",
                }
              : {
                  display: "none",
                }
          }
        >
          <motion.div
            animate={progress > 345 ? { scale: [3, 1] } : {}}
            transition={{ duration: 0.5 }}
            style={{
              maxHeight: "80px",
              width: "80px",
              borderRadius: "50%",
              justifyContent: "stretch",
              display: "flex",
              flex: 1,
            }}
          >
            <img
              src={profile}
              className="rounded-circle"
              style={{ height: "auto", width: "100%" }}
            ></img>
          </motion.div>
          <motion.div
