import React, { createRef, useEffect } from "react";

const canvasRef = createRef();
function getImage(index) {
  return (
    "https://www.apple.com/105/media/us/airpods-pro/2019/1299e2f5_9206_4470_b28e_08307a42f19b/anim/sequence/large/01-hero-lightpass/" +
    index.toString().padStart(4, 0) +
    ".jpg"
  );
}
const updateImage = (imgObj, index, ctx) => {
  if (index === 0 || index === null) {
    index = 1;
  }
  imgObj.src = getImage(index);

  ctx.drawImage(imgObj, 0, 0);
};

function Profile() {
  var imageObj1 = document.getElementById("source");
  const x = 1;
  imageObj1.src = getImage(x);
  const updateCanvas = (imageObj1) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const frameCount = 147;

    canvas.width = imageObj1.width;
    canvas.height = imageObj1.height;

    const html = document.documentElement;
    const scrollTop = html.scrollTop;
    const maxScrollTop = html.scrollHeight - window.innerHeight;
    const scrollFraction = scrollTop / maxScrollTop;
    console.log(scrollTop, scrollFraction);
    const frameIndex = Math.min(
      frameCount - 1,
      Math.floor(scrollFraction * frameCount)
    );

    updateImage(imageObj1, frameIndex + 1, ctx);

    console.log(frameIndex);
  };

  useEffect(() => {
    document.onscroll = async () => {
      await requestAnimationFrame(() => {
        updateCanvas();
      });

      return () => {
        window.removeEventListener(onscroll);
        cancelAnimationFrame();
      };
    };
  }, []);

  //   const draw = (ctx, frameCount) => {

  // const html = document.documentElement;
  // const canvas = document.createElement("canvas");
  // const ctx = canvas.id("profile");
  // const context = canvas.getContext("2d");
  // canvas.width = 1158;
  // canvas.height =1000;
  // // canvas.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
  // // ctx.beginPath();
  // // ctx.fill();
  // const img = new Image();
  // img.src = currentFrame(1);
  // img.onload = function () {
  //   context.drawImage(img, 0, 0);
  // };
  // const updateImage = (index) => {
  //   img.src = currentFrame(index);
  //   ctx.drawImage(img, 0, 0);
  // };

  //   requestAnimationFrame(() => updateImage(frameCount + 1));
  //   console.log(maxScrollTop);
  // });
  //};

  return (
    <canvas ref={canvasRef} style={{}}>
      <img id="source"></img>
    </canvas>
  );
}

export default Profile;
