import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import Profile from "./Profile";
import {
  motion,
  useElementScroll,
  useTransform,
  useViewportScroll,
} from "framer-motion";
import { Component } from "react";
import Aos from "aos";
import "aos/dist/aos.css";
import { auto } from "@popperjs/core";
// const Wrapper = styled.section`
//   display: flex;
//   flex: 1 1;
//   width: 100%;
//   height: 100%;
// `;
// const Row = styled.div`
//   flex: 1;
//   flex-direction: row;
//   width: 100%;
// `;
// const Col = styled.div`
//   flex: 1;
//   flex-direction: column;
//   border: 2px;
// `;
const Section = styled.section`
  justify-conter: center;
  align-item: center;
  display: flex;
  height: 100vh;
  width: 100vw;
`;
const ColumnLeft = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;
const ColumnRight = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex:1
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;

const Home = () => {
  useEffect(() => {
    Aos.init({ duration: 2000 });
  }, []);
  const ref = useRef(null);
  const [progress, setProgress] = useState(0);
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 1], [2, 0]);
  const rotate = useTransform(scrollYProgress, [0, 0], [0, 0]);
  scrollYProgress.onChange(setProgress);

  return (
    <Section ref={ref} className="container-fluid bg-light ">
      <div
        className="container"
        style={{ justifyContent: "center", alignItems: "center" }}
      >
        <div
          class="row"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            width: "25vh",
            height: "25vh",
            fontSize: 32,
            backgroundColor: "hotpink",
            scale,
            rotate,
          }}
        >
          <div class="col">
            <ColumnLeft clasName="bg-light">
              <motion.figure
                animate={{ scale: [8, 0.5] }}
                transition={{ duration: 0.9 }}
              >
                <h2 class="display-4">Suman Karanjit</h2>
                <h4 class=" " style={{ color: "#6c757d" }}>
                  Software Developer
                </h4>
              </motion.figure>
            </ColumnLeft>
          </div>
          <div class="col">
            <ColumnRight clasName="bg-light">
              <motion.figure
                animate={{ scale: [8, 0.5], offsetRotate: 20 }}
                transition={{ duration: 0.9 }}
              >
                <h2 class="display-4">Suman Karanjit</h2>
                <h4 class=" " style={{ color: "#6c757d" }}>
                  Software Developer
                </h4>
              </motion.figure>
            </ColumnRight>
          </div>
        </div>

        <div
          data-aos="fade-left"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            height: "50vh",
          }}
        >
          <motion.div style={{}}>{progress.toFixed(2)}</motion.div>
        </div>
      </div>
    </Section>
  );
};

export default Home;
