import React from "react";
import {
  motion,
  motionValue,
  useTransform,
  useViewportScroll,
} from "framer-motion";
import Aos from "aos";
import "aos/dist/aos.css";
function Edu(props) {
  return (
    <section
      style={{
        width: "80vw",
        flex: 1,
        display: "flex",
        flexDirection: "row",
        justifyContent: "center",
        top: 0,
        alignContent: "center",
        marginLeft: "20vw",
      }}
      className="bg-light "
    >
      <motion.div className="text-center">
        <h2
          data-aos="fade-right"
          style={{
            paddingBottom: "10px",
          }}
          class="display-4"
        >
          Education
        </h2>
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: [0, 0.5, 1] }}
          transition={{ duration: 5 }}
        >
          <h3>Bachelor's in Computer Science</h3>
          MSUM, Moorhead, MN, USA (Graduated May 2021)
        </motion.div>
        <h3>Diploma in Computer Information Technology</h3>
        National Technology Institute, Auckland, New Zealand (Graduated March
        2012)
      </motion.div>
    </section>
  );
}

export default Edu;
