import React, { Component } from "react";
import {
  Container,
  Navbar,
  Nav,
  NavDropdown,
  Button,
  Modal,
  FormText,
  Col,
  Row,
  Form,
  Text,
} from "react-bootstrap";
class MenuBar extends Component {
  state = {
    navExpanded: false,
    setShow: false,
    haveAccount: false,
    registered: false,
  };

  setExpanded() {
    this.setState({
      navExpanded: this.state.navExpanded === false ? "exapanded" : false,
    });
  }

  handleClose() {
    this.setState({
      setShow: false,
    });
  }
  handleShow() {
    this.setState({
      setShow: true,
    });
  }
  loadLogin() {
    this.setState({
      registered: !this.state.registered,
    });
  }
  loginPage() {
    return (
      <Form>
        <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
          <Form.Label column sm="2">
            Email
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext defaultValue="email@example.com" />
          </Col>
        </Form.Group>

        <Form.Group as={Row} className="mb-3" controlId="formPlaintextPassword">
          <Form.Label column sm="2">
            Password
          </Form.Label>
          <Col sm="10">
            <Form.Control type="password" placeholder="Password" />
          </Col>
        </Form.Group>
        <div style={{ textAlign: "center" }}>
          <Button
            style={{ border: 0, textAlign: "center" }}
            onClick={() => {
              this.loadLogin();
            }}
          >
            Need to register?
          </Button>
        </div>
      </Form>
    );
  }

  registerPage() {
    return (
      <Form>
        <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
          <Form.Label column sm="2">
            Name
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext defaultValue="First M Last" />
          </Col>
        </Form.Group>
        <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
          <Form.Label column sm="2">
            Phone
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext defaultValue="111-111-1111" />
          </Col>
        </Form.Group>
        <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
          <Form.Label column sm="2">
            Email
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext defaultValue="email@example.com" />
          </Col>
        </Form.Group>

        <Form.Group as={Row} className="mb-3" controlId="formPlaintextPassword">
          <Form.Label column sm="2">
            Password
          </Form.Label>
          <Col sm="10">
            <Form.Control type="password" placeholder="Password" />
          </Col>
        </Form.Group>
        <div
          style={{
            textAlign: "right",
            flex: 1,
            flexBasis: 1,
            padding: 10,
          }}
        >
          <Button
            style={{
              textAlign: "center",
            }}
            onClick={() => this.handleClose()}
          >
            Close
          </Button>
          <Button variant="primary" onClick={() => this.handleClose()}>
            Register
          </Button>
        </div>

        <div style={{ textAlign: "center" }}>
          <Button
            style={{ border: 0, textAlign: "center" }}
            onClick={() => {
              this.loadLogin();
            }}
          >
            Already registered?
          </Button>
        </div>
      </Form>
    );
  }
  render() {
    return (
      <header className="fixed-top">
        <Navbar
          bg="dark"
          variant="dark"
          expand="lg"
          sticky="top"
          id="sidebar"
          expanded={this.state.navExpanded}
        >
          <Container fluid style={{ marginLeft: 40, marginRight: 40 }}>
            <Navbar.Toggle
              onClick={() =>
                setTimeout(() => {
                  this.setExpanded();
                }, 150)
              }
              aria-controls="basic-navbar-nav"
            />
            <Navbar.Brand href="#home">Logo</Navbar.Brand>

            <Navbar.Collapse id="sidebar" className="justify-content-end">
              <Nav
                onSelect={() =>
                  this.setState({
                    navExpanded: false,
                  })
                }
                defaultActiveKey="#home"
              >
                <Nav.Link href="#home">Home</Nav.Link>
                <Nav.Link href="#link">Link</Nav.Link>
                <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                  <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2">
                    Another action
                  </NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3">
                    Something
                  </NavDropdown.Item>
                  <NavDropdown.Divider />
                  <NavDropdown.Item href="#action/3.4">
                    Separated link
                  </NavDropdown.Item>
                </NavDropdown>
                <Button variant="primary" onClick={() => this.handleShow()}>
                  Register/Login
                </Button>
              </Nav>
            </Navbar.Collapse>
            <Modal show={this.state.setShow} onHide={() => this.handleClose()}>
              <Modal.Header closeButton>
                <Modal.Title>Registration Page</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                {this.state.registered == false
                  ? this.registerPage()
                  : this.loginPage()}
              </Modal.Body>
            </Modal>
          </Container>
        </Navbar>
      </header>
    );
  }
}

export default Header;
