import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import Profile from "./Profile";
import {
  motion,
  useElementScroll,
  useTransform,
  useViewportScroll,
} from "framer-motion";
import { Component } from "react";
import Aos from "aos";
import "aos/dist/aos.css";
import { auto } from "@popperjs/core";
// const Wrapper = styled.section`
//   display: flex;
//   flex: 1 1;
//   width: 100%;
//   height: 100%;
// `;
// const Row = styled.div`
//   flex: 1;
//   flex-direction: row;
//   width: 100%;
// `;
// const Col = styled.div`
//   flex: 1;
//   flex-direction: column;
//   border: 2px;
// `;
const Section = styled.section`
  justify-conter: center;
  align-item: center;
  display: flex;
  position: "absolute";
  width: 100vw;
`;
const ColumnLeft = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;
const ColumnRight = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex:1
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;

const Home = () => {
  useEffect(() => {
    Aos.init({ duration: 2000 });
  }, []);
  const ref = useRef(null);
  const [progress, setProgress] = useState(0);
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 1], [1, 0]);
  const rotate = useTransform(scrollYProgress, [0, 0], [0, 0]);
  scrollYProgress.onChange(setProgress);

  return (
    <div style={{ height: "100vh", width: "100vw" }}>
      <motion.Section
        style={{
          scale,
          rotate,
        }}
        ref={ref}
        className="bg-light "
      >
        <motion.div className="">
          <div data-aos="fade-left" class="row">
            <div class="col">
              <ColumnLeft clasName="bg-light">
                <motion.figure
                  animate={{ scale: [8, 0.5] }}
                  transition={{ duration: 0.9 }}
                >
                  <h2 class="display-4">Suman Karanjit</h2>
                  <h4 class=" " style={{ color: "#6c757d" }}>
                    Software Developer
                  </h4>
                </motion.figure>
              </ColumnLeft>
            </div>
          </div>
        </motion.div>
      </motion.Section>
    </div>
  );
};

export default Home;
