import { PromiseResolve } from "es-abstract/es2018";
import React, { Component, createRef, useEffect } from "react";
const checkImage = (path) =>
  new Promise((resolve) => {
    const img = new Image();
    img.onload = () => resolve(path);

    img.src = path;
  });

class Profile extends Component {
  constructor() {
    super();
    this.canvasRef = createRef;
  }
  getImage(index) {
    // "https://www.apple.com/105/media/us/airpods-pro/2019/1299e2f5_9206_4470_b28e_08307a42f19b/anim/sequence/large/01-hero-lightpass/" +
    // index.toString().padStart(4, 0) +
    // ".jpg"
    const imgUrl =
      process.env.PUBLIC_URL + "/img/img" + index.toString() + ".png";
    const myPromise = checkImage(imgUrl);
    myPromise
      .then((response) => console.log(response))
      .catch((error) => console.error(error));
  }

  updateImage = async (imgObj, index, ctx) => {
    if (index === 0 || index === null) {
      index = 1;
    }
    imgObj.src = await this.getImage(index);
    console.log(imgObj);
    ctx.drawImage(imgObj, 0, 0);
  };
  async updateCanvas() {
    const canvas = this.canvasRef.current;
    const ctx = canvas.getContext("2d");
    const frameCount = 7;
    var imageObj1 = document.getElementById("source");

    canvas.width = imageObj1.width;
    canvas.height = imageObj1.height;

    const html = document.documentElement;
    const scrollTop = html.scrollTop;
    const maxScrollTop = html.scrollHeight - window.innerHeight;
    const scrollFraction = scrollTop / maxScrollTop;
    console.log(scrollTop, scrollFraction);
    const frameIndex = Math.min(
      frameCount - 1,
      Math.floor(scrollFraction * frameCount)
    );

    await this.updateImage(imageObj1, frameIndex + 1, ctx);

    console.log(frameIndex);
  }

  getAllImg = async () => {
    var x = 0;
    while (x != 7) {
      x++;
      const imgObj = new Image();

      imgObj.src = await this.getImage(x);
    }
  };
  componentDidMount() {
    this.updateCanvas;
  }
  render() {
    return (
      <canvas ref={this.canvasRef}>
        <img id="source" />
      </canvas>
    );
  }
}
export default Profile;
