import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import profile from "./../assets/img/profile.png";
import { SocialIcon } from "react-social-icons";
import Edu from "./../components/edu.js";
import Exp from "./../components/exp.js";
import {
  motion,
  motionValue,
  useTransform,
  useViewportScroll,
} from "framer-motion";
import Aos from "aos";
import "aos/dist/aos.css";

const Section = styled.section`
  justify-conter: center;
  align-item: center;
  flex: "1";
  display: flex;
`;
const Container1 = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;
const ColumnRight = styled.div`
  padding: 5rem 2rem;
  display: flex;
  flex:1
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;

const contentOffsetY = motionValue(0);

const Home = () => {
  const ref = useRef();
  const [progress, setProgress] = useState(0);
  const { scrollY, scrollYProgress } = useViewportScroll();

  useEffect(() => {
    Aos.init({ duration: 2000 });
    // var x = scrollYProgress.onChange((v) => v);
    // return x;
    return scrollY.onChange((v) => setProgress(v));
  }, [scrollYProgress, scrollY]);
  console.log(progress);
  const scale = useTransform(scrollYProgress, [1, 0], [0.8, 1], {
    clamp: false,
  });

  return (
    <div>
      <motion.Section
        style={{
          scale,
        }}
        id="top-page"
        ref={ref}
        className="bg-dark"
      >
        <Container1
          style={{
            flex: 1,
            minHeight: "100vh",
            flexDirection: "column",
            flexWrap: "wrap",
            justifyContent: "center",
            alignItems: "center",
          }}
          data-aos="fade-left"
          clasName="bg-dark vh-100 "
        >
          <motion.div
            animate={{ scale: [2, 1] }}
            style={{
              maxHeight: "300px",
              maxWidth: "300px",
              borderRadius: "50%",
              marginBottom: "15px",
              flex: 1,
              justifyContent: "flex-end",
              alignItems: "center",
              alignContent: "space-around",
            }}
          >
            <img
              src={profile}
              className="rounded-circle"
              style={{ height: "auto", width: "100%" }}
            ></img>
          </motion.div>
          <h2
            style={{
              color: "#ffff",

              textAlign: "center",
            }}
            class="display-3  "
          >
            Suman Karanjit
          </h2>

          <h6 class=" " style={{ color: "#6c757d", textAlign: "center" }}>
            Aspiring software developer experienced in object-oriented
            programming; developing, testing and debugging code; designing
            interfaces; and administering systems and networks.
          </h6>
        </Container1>
      </motion.Section>

      <ul
        class="nav nav-pills mb-3"
        id="pills-tab"
        role="tablist"
        style={{ flex: 1, display: "flex", justifyContent: "center" }}
      >
        <li class="nav-item" role="presentation">
          <motion.button
            whileFocus={{ scaleX: [1.2, 1] }}
            whileTap={{ scaleZ: [1.2, 1] }}
            class="nav-link active"
            id="pills-home-tab"
            data-bs-toggle="pill"
            data-bs-target="#Edu"
            type="button"
            role="tab"
            aria-controls="Edu"
            aria-selected="true"
          >
            Education
          </motion.button>
        </li>
        <li class="nav-item" role="presentation">
          <motion.button
            whileFocus={{ scaleX: [1.2, 1] }}
            whileTap={{ scaleZ: [1.2, 1] }}
            class="nav-link"
            id="pills-profile-tab"
            data-bs-toggle="pill"
            data-bs-target="#Exp"
            type="button"
            role="tab"
            aria-controls="Exp"
            aria-selected="false"
          >
            Professional Experiences
          </motion.button>
        </li>
      </ul>
      <div class="tab-content" id="pills-tabContent">
        <div
          class="tab-pane fade show active"
          id="Edu"
          role="tabpanel"
          aria-labelledby="Edu-tab"
        >
          <Edu />
        </div>
        <div
          class="tab-pane fade"
          id="Exp"
          role="tabpanel"
          aria-labelledby="Exp-tab"
        >
          <Exp />
        </div>
      </div>
    </div>
  );
};

export default Home;
