import { request } from "dom-helpers/cjs/animationFrame";
import React, {
  Component,
  createRef,
  ReactPropTypes,
  useLayoutEffect,
} from "react";
import Canvas from "./../components/canvas";
import ReactDOM from "react-dom";
import { WindowsBalloon } from "node-notifier";

const canvasRef = createRef();
function getImage(index) {
  return (
    "https://www.apple.com/105/media/us/airpods-pro/2019/1299e2f5_9206_4470_b28e_08307a42f19b/anim/sequence/large/01-hero-lightpass/" +
    index.toString().padStart(4, 0) +
    ".jpg"
  );
}
const updateImage = (imgObj, index, ctx) => {
  imgObj.src = getImage(index);
  imgObj.height = document.documentElement.innerHeight;
  imgObj.width = document.documentElement.innerWidth;
  ctx.drawImage(imgObj, 0, 0);
};

