    const html = document.documentElement;
    const canvas = canvasRef.current;
    const cc = document.getElementById("canvasContainer");

    const ctx = canvas.getContext("2d");
    const frameCount = 147;

    if (canvas) {
      canvas.width = 1158;
      canvas.height = 770;

